import React, { useEffect } from "react";
import Image from "next/image";
import { DrawSVGPlugin } from "gsap/dist/DrawSVGPlugin";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
import Router, { useRouter } from "next/router";
import { useRef } from "react";
import { gsap } from "gsap";

gsap.registerPlugin(ScrollTrigger, DrawSVGPlugin);

function PageLinks({ logo, text, order, route, color }) {
  const router = useRouter();

  const button = useRef();

  const handleClick = () => {
    router.push(route);
  };

  let ctx = gsap.context(() => {
    useEffect(() => {
      let tl = gsap.timeline();
      tl.fromTo(
        "#button-path",
        {
          drawSVG: "0%",
        },
        {
          drawSVG: "100%",
          // strokeDasharray: "1",
          duration: 40,
        }
      );

      return () => {
        ctx.revert();
      };
    }, []);
  }, button);

  return (
    <div
      ref={button}
      onClick={handleClick}
      style={{ gridArea: `area-${order}` }}
      className={`relative w-[400px] flex flex-row  gap-10 hover:cursor-pointer ${
        order !== null ? "self-end" : "self-start"
      }`}
    >
      <div className="relative flex items-center w-[215px]">
        <BoxSvg hexcode={"#0057C1"} />
        <div className="absolute ml-[15px] w-[62px] hover:rotate-[360deg] transition-transform duration-[0.5s] aspect-square">
          <Image fill style={{ objectFit: "cover" }} src={logo} />
        </div>
      </div>

      <h3 className={`text-${color} text-[24px] `}>{text}</h3>
    </div>
  );
}

export default PageLinks;

